import React from "react";

export const NavBar = ({ selectedOption,handleChange } ) => {


    const setOptions = () => {
        const options = [];

        const labelArr = ["צור קשר","מאמרים", "? מה אני מלמד", " אני ואני מאמין", "ברוכים הבאים"];

        labelArr.map((i)=>
         options.push({id: `st-control-${i + 1}`, for : `st-panel-${i + 1}`, label : labelArr[i] }) );

        /* for(let i = 1; i <= labelArr.length ;++i ) {
            options.push({id: `st-control-${i}`, for : `st-panel-${i}`, label : labelArr[i - 1] });  
        } */

        return options;
    }


    const options = setOptions();              

    return (  
        options.map((option)=> {
            return (
            <React.Fragment key={option.id}>
            <input
              type="radio"
              name="radio-set"
              id = {option.id}
              for={option.for}
              checked={ selectedOption === option.id }
              onChange={ handleChange }
              />
            <a href={`#${option.for}`}>{option.label}</a>
          </React.Fragment>
        )
        })
    )
}
