import React , { useState } from 'react';
import "./App.css"

import { sectionArr } from "./sections/sectionArr"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faComputer } from '@fortawesome/free-solid-svg-icons';
import { NavBar } from './components/navBar';

NavBar

const Section = ({ section }) => (
  <section className={section.class} id={`st-panel-${section.id}`}>
    <div className="st-deco" data-icon={ section.icon }></div>
    <h2>{section.title} </h2>
    <p>{section.text}</p>

  </section>
);

const App = () => {
  const [selectedOption, setSelectedOption] = useState("st-control-5");

  const handleChange = (event) => {
    setSelectedOption(event.target.id);
  };

  const setOptions = (labelArr) => {
    const options = [];

    for(let i = 1; i <= labelArr.length ;++i ) {
      options.push({id: `st-control-${i}`, for : `st-panel-${i}`, label : labelArr[i - 1] });  
    }

    return options;
  }

  const lables = ["צור קשר","מאמרים", "? מה אני מלמד", " אני ואני מאמין", "ברוכים הבאים"];

  const options = setOptions(lables);


  return (
    <React.Fragment>
      <div class="container">
        <div class="st-container">

        {/* {options.map((option) => (
        <React.Fragment key={option.id}>
        <input
          type="radio"
          name="radio-set"
          id = {option.id}
          for={option.for}
          checked={ selectedOption === option.id }
          onChange={ handleChange }
        />
        <a href={`#${option.for}`}>{option.label}</a>
      </React.Fragment>
    ))}
 */}
        <div class="st-scroll">
          {sectionArr.map(section => (
            <Section key={section.id} section={section} />
          ))}

          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

export default App;
