import React , { useState } from 'react';
import "./App.css"

import { sectionArr } from "./sections/sectionArr"
import { NavBar } from './components/navBar';
import { Sections } from './components/sections';

const Section = ({ section }) => (
  <section className={section.class} id={`st-panel-${section.id}`}>
    <div className="st-deco" data-icon={ section.icon }></div>
    <h2>{section.title} </h2>
    <p>{section.text}</p>

  </section>
);

const App = () => {
  const [selectedOption, setSelectedOption] = useState("st-control-5");

  const handleChange = (event) => {
    setSelectedOption(event.target.id);
  };

  return (
    <React.Fragment>
      <div class="container">
        <div class="st-container">

        <NavBar selectedOption = {selectedOption} handleChange = { handleChange }  />

        <Sections/>
        </div>



      </div>
    </React.Fragment>
  );
};

export default App;
