import React , {  useState } from 'react';
import "./App.css"

import { sectionArr } from "./sections/sectionArr"

const Section = ({ section }) => (
  <section className={section.class} id={`st-panel-${section.id}`}>
    <div className="st-deco" data-icon={section.icon}></div>
    <h2>{section.title}</h2>
      {console.log("i am here")}
    <p>{section.text}</p>
  </section>
);



const App = () => {

  const [checkedNum,setCheckedNum] = useState(1);

  const moveForward = () => {
    setCheckedNum(checkedNum + 1);
  }

  return (
    <React.Fragment>
      <div class="container">
			<div class="st-container">
        <input type="radio" name="radio-set" 
              checked = { checkedNum == 1 ? "checked" : "" }   id="st-control-1" for="st-panel-1"/>
        <a onClick={ () => moveForward() } href="#st-panel-1">Serendipity</a>
        <input 
        type="radio"
        checked = { checkedNum == 2 ? "checked" : "" }
        name="radio-set" id="st-control-2" for="st-panel-2"/>

        <a href="#st-panel-2">Happiness</a>
        <input type="radio" name="radio-set" id="st-control-3" for="st-panel-3"/>
        <a href="#st-panel-3">Tranquillity</a>
        <input type="radio" name="radio-set" id="st-control-4" for="st-panel-4"/>
        <a href="#st-panel-4">Positivity</a>
        <input type="radio" checked = "checked" name="radio-set" id="st-control-5" for="st-panel-5"/>
        <a href="#st-panel-5">Passion</a>

			  <div class="st-scroll">
        {sectionArr.map(section => (
          <Section key={section.id} section= {section} />
        ))}
				</div>

			</div>

</div>
    </React.Fragment>
  )
}

export default App;
