import React , { useState } from 'react';
import "./App.css"

import { sectionArr } from "./sections/sectionArr"

const Section = ({ section }) => (
  <section className={section.class} id={`st-panel-${section.id}`}>
    <div className="st-deco" data-icon={section.icon}></div>
    <h2>{section.title}</h2>
      {console.log("i am here")}
    <p>{section.text}</p>
  </section>
);

const App = () => {
  const [selectedOption, setSelectedOption] = useState("st-control-1");

  const handleChange = (event) => {
    setSelectedOption(event.target.id);

  };


  const setOptions = () => {
    const options = [];

    for(let i = 1; i <=5 ;++i ) {
      options.push({id: `st-control-${i}`, for : `st-panel-1`})  
    }

  }

  const options = [
    { id: "st-control-1", for: "st-panel-1", label: "Serendipity" },
    { id: "st-control-2", for: "st-panel-2", label: "Happiness" },
    { id: "st-control-3", for: "st-panel-3", label: "Tranquillity" },
    { id: "st-control-4", for: "st-panel-4", label: "Positivity" },
    { id: "st-control-5", for: "st-panel-5", label: "Passion" },
  ];

  return (
    <React.Fragment>
      <div class="container">
        <div class="st-container">


        {options.map((option, index) => (
        <React.Fragment key={option.id}>
        <input
          type="radio"
          name="radio-set"
          id={option.id}
          for={option.for}
          checked={selectedOption === option.id}
          onChange={handleChange}
        />
        <a href={`#${option.for}`}>{option.label}</a>
      </React.Fragment>
    ))}



          <div class="st-scroll">
            {sectionArr.map(section => (
              <Section key={section.id} section={section} />
            ))}

          </div>
        </div>
      </div>
    </React.Fragment>
  );
};

export default App;
