import "./App.css"

import { sectionArr } from "./sections/sectionArr"

const Section = ({ section }) => (

  <section className={section.class} id={`st-panel-${section.id}`}>
    <div className="st-deco" data-icon={section.icon}></div>
    <h2>{section.title}</h2>
      {console.log("i am here")}
    <p>{section.text}</p>
  </section>
);
const App = () => {
const [activeIndex, setActiveIndex] = useState(1);

const handleClick = (index) => {
setActiveIndex(index);
};

return (
<React.Fragment>
<div class="container">
    <div class="st-container">
    <a href="#st-panel-1" onClick={() => handleClick(1)}>Serendipity</a>
    <a href="#st-panel-2" onClick={() => handleClick(2)}>Happiness</a>
    <a href="#st-panel-3" onClick={() => handleClick(3)}>Tranquillity</a>
    <a href="#st-panel-4" onClick={() => handleClick(4)}>Positivity</a>
    <a href="#st-panel-5" onClick={() => handleClick(5)}>Passion</a>



<div class="st-scroll">
    {sectionArr.map((section, index) => (
      <Section key={section.id} section= {section} />
    ))}
			</div>

		</div>


    </div>
    </React.Fragment>
  )
}
export default App;






