import React from "react";

export const NavBar = ({ selectedOption,handleChange } ) => {

    const options = () => {
        const optionsSetter = [];

        const labelArr = ["צור קשר","מאמרים", "? מה אני מלמד", " אני ואני מאמין", "ברוכים הבאים"];

        labelArr.map((elem,i) =>
        optionsSetter.push({id: `st-control-${i + 1}`, for : `st-panel-${i + 1}`, label : labelArr[i]}));

        return optionsSetter;
    }

    return (  
        options.map((option)=> {
            return (
            <React.Fragment key={option.id}>
            <input
              type="radio"
              name="radio-set"
              id = {option.id}
              for={option.for}
              checked={ selectedOption === option.id }
              onChange={ handleChange }
              />
            <a href={`#${option.for}`}>{option.label}</a>
          </React.Fragment>
        )
        })
    )
}
